// client/src/components/DrawingCanvas.js
import React, { useEffect, useRef, useState } from "react";
import socket from "../socket";

const DrawingCanvas = ({ roomId, color, width }) => {
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      console.log("ðŸ–¼ï¸ Canvas resized to", canvas.width, canvas.height);
    };

    resizeCanvas();
    window.addEventListener("resize", resizeCanvas);

    const drawPath = ({ path, color, width }) => {
      if (!path.length) return;
      ctx.strokeStyle = color;
      ctx.lineWidth = width;
      ctx.lineJoin = "round";
      ctx.lineCap = "round";
      ctx.beginPath();
      ctx.moveTo(path[0].x, path[0].y);
      for (let i = 1; i < path.length; i++) {
        ctx.lineTo(path[i].x, path[i].y);
      }
      ctx.stroke();
    };

    socket.on("draw-move", drawPath);
    socket.on("clear-canvas", () => ctx.clearRect(0, 0, canvas.width, canvas.height));

    return () => {
      window.removeEventListener("resize", resizeCanvas);
      socket.off("draw-move");
      socket.off("clear-canvas");
    };
  }, []);

  const path = useRef([]);

  const startDrawing = (e) => {
    setDrawing(true);
    path.current = [getCoordinates(e)];
  };

  const draw = (e) => {
    if (!drawing) return;
    const point = getCoordinates(e);
    path.current.push(point);

    // Draw immediately on local canvas
    const ctx = canvasRef.current.getContext("2d");
    const lastPoint = path.current[path.current.length - 2];
    if (lastPoint) {
      ctx.strokeStyle = color;
      ctx.lineWidth = width;
      ctx.lineJoin = "round";
      ctx.lineCap = "round";
      ctx.beginPath();
      ctx.moveTo(lastPoint.x, lastPoint.y);
      ctx.lineTo(point.x, point.y);
      ctx.stroke();
    }

    socket.emit("draw-move", {
      path: [lastPoint, point],
      color,
      width,
    });
  };

  const endDrawing = () => {
    setDrawing(false);
    socket.emit("draw-end", {
      path: path.current,
      color,
      width,
    });
    path.current = [];
  };

  const getCoordinates = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const clientX = e.clientX || e.touches?.[0]?.clientX;
    const clientY = e.clientY || e.touches?.[0]?.clientY;

    return {
      x: clientX - rect.left,
      y: clientY - rect.top,
    };
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        zIndex: 1,
        backgroundColor: "#ffffff", // visible canvas
        touchAction: "none",
      }}
      onMouseDown={startDrawing}
      onMouseMove={draw}
      onMouseUp={endDrawing}
      onMouseLeave={endDrawing}
      onTouchStart={startDrawing}
      onTouchMove={draw}
      onTouchEnd={endDrawing}
    />
  );
};

export default DrawingCanvas;
