// client/src/components/Whiteboard.js
import React, { useEffect, useState } from "react";
import socket from "../socket";
import DrawingCanvas from "./DrawingCanvas";
import Toolbar from "./Toolbar";
import UserCursors from "./UserCursors";

const Whiteboard = ({ roomId }) => {
  const [color, setColor] = useState("black");
  const [width, setWidth] = useState(2);
  const [userCount, setUserCount] = useState(1);
  const [cursors, setCursors] = useState({});

  useEffect(() => {
    socket.emit("join-room", roomId);

    socket.on("user-count", (count) => setUserCount(count));
    socket.on("cursor-update", ({ x, y, id }) => {
      setCursors((prev) => ({
        ...prev,
        [id]: { x, y, timestamp: Date.now() },
      }));
    });

    return () => {
      socket.emit("leave-room", roomId);
      socket.off("user-count");
      socket.off("cursor-update");
    };
  }, [roomId]);

  return (
    <div
      style={{
        position: "relative",
        width: "100vw",
        height: "100vh",
        overflow: "hidden",
        background: "#ffffff",
      }}
    >
      {/* Drawing Canvas (zIndex 1) */}
      <DrawingCanvas roomId={roomId} color={color} width={width} />

      {/* User Cursors (zIndex 2) */}
      <UserCursors cursors={cursors} />

      {/* Toolbar (zIndex 3) */}
      <div style={{ position: "absolute", top: 0, left: 0, zIndex: 3 }}>
        <Toolbar
          color={color}
          setColor={setColor}
          width={width}
          setWidth={setWidth}
        />
      </div>

      {/* User count (top right corner) */}
      <div
        style={{
          position: "absolute",
          top: 10,
          right: 10,
          zIndex: 4,
          background: "rgba(255,255,255,0.8)",
          padding: "4px 8px",
          borderRadius: "8px",
          fontSize: "14px",
        }}
      >
        ðŸŸ¢ Users in room: {userCount}
      </div>
    </div>
  );
};

export default Whiteboard;
