import React, { useEffect, useRef, useState } from "react";
import socket from "../socket";

const DrawingCanvas = ({ roomId, color, width }) => {
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);

  // Util to get coordinates from mouse/touch
  const getCoordinates = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const clientX = e.clientX || e.touches?.[0]?.clientX;
    const clientY = e.clientY || e.touches?.[0]?.clientY;
    return {
      x: clientX - rect.left,
      y: clientY - rect.top,
    };
  };

  // Local canvas draw utility
  const drawPath = ({ path, color, width }) => {
    const ctx = canvasRef.current.getContext("2d");
    if (!path || path.length < 2) return;
    ctx.strokeStyle = color;
    ctx.lineWidth = width;
    ctx.lineJoin = "round";
    ctx.lineCap = "round";
    ctx.beginPath();
    ctx.moveTo(path[0].x, path[0].y);
    for (let i = 1; i < path.length; i++) {
      ctx.lineTo(path[i].x, path[i].y);
    }
    ctx.stroke();
  };

  // Setup listeners
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      console.log("ðŸ–¼ï¸ Canvas resized to", canvas.width, canvas.height);
    };

    resizeCanvas();
    window.addEventListener("resize", resizeCanvas);

    // Receive individual draw strokes
    socket.on("draw-move", drawPath);

    // Clear canvas from remote user
    socket.on("clear-canvas", () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    });

    // ðŸ”¥ Load full canvas history
    socket.on("load-drawing", (drawingData) => {
      console.log("ðŸ—‚ï¸ Loading previous drawing history...");
      drawingData.forEach((cmd) => {
        if (cmd.type === "stroke" && cmd.data?.path?.length) {
          drawPath({ path: cmd.data.path, color: cmd.data.color, width: cmd.data.width });
        } else if (cmd.type === "clear") {
          ctx.clearRect(0, 0, canvas.width, canvas.height);
        }
      });
    });

    return () => {
      window.removeEventListener("resize", resizeCanvas);
      socket.off("draw-move");
      socket.off("clear-canvas");
      socket.off("load-drawing");
    };
  }, []);

  // Drawing handlers
  const path = useRef([]);

  const startDrawing = (e) => {
    setDrawing(true);
    path.current = [getCoordinates(e)];
  };

  const draw = (e) => {
    if (!drawing) return;

    const point = getCoordinates(e);
    const lastPoint = path.current[path.current.length - 1];
    path.current.push(point);

    // Local stroke
    if (lastPoint) {
      drawPath({ path: [lastPoint, point], color, width });
    }

    // Emit partial stroke
    socket.emit("draw-move", {
      path: [lastPoint, point],
      color,
      width,
    });
  };

  const endDrawing = () => {
    setDrawing(false);

    socket.emit("draw-end", {
      path: path.current,
      color,
      width,
    });

    path.current = [];
  };

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        zIndex: 1,
        backgroundColor: "#ffffff",
        touchAction: "none",
      }}
      onMouseDown={startDrawing}
      onMouseMove={draw}
      onMouseUp={endDrawing}
      onMouseLeave={endDrawing}
      onTouchStart={startDrawing}
      onTouchMove={draw}
      onTouchEnd={endDrawing}
    />
  );
};

export default DrawingCanvas;
